import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import BottomNavigation from './BottomNavigation';
import BottomNavigationAction from './BottomNavigationActioin';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';

const useStyles = makeStyles({
  root: {
    width: '425px',
    display: 'flex',
    justifyContent: 'center',
    backgroundColor: '#fff',
  },
  button: {
    width: '33.33%',
    padding: '1em',
    display: 'flex',
    height: '50px',
    alignItems: 'center',
    justifyContent: 'center',
  }
});

export default function BottomBar() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  const [barNumber, setBarNumber] = React.useState(1);
  const selectedColor = 'aliceblue';
  const defaultColor = 'grey';

  const onclickHandler = (e) => {
    console.log(e)
    const index = e.target.id 
    console.log(index)
    setBarNumber(index)
  }

  const colorHandler = (index) => {
    console.log(index)
    if (index == barNumber) {
      return selectedColor
    } else {
      return defaultColor
    }
  }

  return (
    <div className={classes.root}>
      <div className={classes.button} style={{color:`${colorHandler(1)}`}}><RestoreIcon id="1" onClick={(e) =>onclickHandler(e)} style={{height:'50%', width:'auto'}}/></div>
      <div className={classes.button} id="2" onClick={(e) =>onclickHandler(e)} style={{color:`${colorHandler(2)}`}}><FavoriteIcon style={{height:'50%', width:'auto'}}/></div>
      <div className={classes.button} id="3" onClick={(e) =>onclickHandler(e)} style={{color:`${colorHandler(3)}`}}><LocationOnIcon style={{height:'50%', width:'auto'}}/></div>
    </div>
    // <span>
    //   <BottomNavigation>

    //     <RestoreIcon />
    //     <FavoriteIcon />
    //     <LocationOnIcon />
    //     <BottomNavigationAction label="Recents" icon={<RestoreIcon />} />
    //     <BottomNavigationAction label="Favorites" icon={<FavoriteIcon />} />
    //     <BottomNavigationAction label="Nearby" icon={<LocationOnIcon />} />
    //   </BottomNavigation>
    // </span>
  );
}
