import React, { useState, useEffect } from 'react';
import Card from './InterestListCard'
import './InterestList.css'
import Divider from '@material-ui/core/Divider';
import { Link } from 'react-router-dom';
import SearchBox from './SearchBox';

const axios = require('axios');

function InterestList() {
    const [videoData, setVideoData] = useState([])
    useEffect(() => {
        axios.get(`/api/v1/coin/list/`)
            .then((response) => {
                console.log(response.data)
                setVideoData(response.data);
            }).catch((error) => {
                console.log(error);
            });
     }, [])
    const items = videoData
    const [search, setSearch] = useState([])
    const searchSpace=(event)=>{
        let keyword = event.target.value;
        setSearch(keyword);
        console.log(keyword);
      }
    const cancel=()=>{
        setSearch('');
    }
    const ItemList = items && items.filter((item)=>{
        if(search==''){
            return item
        }
        else if(item.coin_name.toLowerCase().includes(search.toLowerCase()) || item.kr_name.includes(search)){
            return item
        }
    }).map((item) =>
    (<li><Link to={"/detail/"+item.ticker}  style={{textDecoration:'none',color:'inherit'}}><Card name={item.coin_name ?? ''} price={item.coin_price[0].price ?? ''} pricerate={item.coin_price[0].day_change ?? ''} img={item.coin_img   ?? ''}></Card><Divider/></Link></li>
    )
    );
    return (
        <div className="interestList">
            <div style={{display:'flex',boxSizing:'border-box',margin: '0 0.3em',}}>
                <input type="text" class="form__field" placeholder="Search" onChange={(e)=>searchSpace(e)}/>
                <button onClick={cancel()}>취소</button>
            </div>
            <div>
                <span className="interestListText">관심목록</span>
            </div>
            <div className="interestListBox">
                <ul>
                    {ItemList}
                </ul>
            </div>
        </div>
    );
}

export default InterestList
