import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import BottomNavigation from './BottomNavigation';
import BottomNavigationAction from './BottomNavigationActioin';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';

const useStyles = makeStyles({
  root: {
    width: 500,
  },
});

export default function BottomBar() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  const [barNumber, setBarNumber] = React.useState(1);
  const selectedColor = 'aliceblue';
  const defaultColor = 'grey';

  const onclickHanler = (nowBarNumber) => {
    console.log(nowBarNumber.target.id)
    setBarNumber(nowBarNumber.target.id)
  }

  const colorHandler = (e) => {
    if (e == barNumber) {
      return setBarNumber
    } else {
      return defaultColor
    }
  }

  return (
    <div>
      <span>{colorHandler()}</span>
      <div id="1" onClick={(e) =>onclickHanler(e)} style={{color:`${colorHandler(1)}`}} >click</div>
      <div id="2" onClick={(e) =>onclickHanler(e)} style={{color:`${colorHandler(1)}`}}>click</div>
      <div id="3" onClick={(e) =>onclickHanler(e)} style={{color:`${colorHandler(1)}`}}>click</div>
    </div>
    // <span>
    //   <BottomNavigation>

    //     <RestoreIcon />
    //     <FavoriteIcon />
    //     <LocationOnIcon />
    //     <BottomNavigationAction label="Recents" icon={<RestoreIcon />} />
    //     <BottomNavigationAction label="Favorites" icon={<FavoriteIcon />} />
    //     <BottomNavigationAction label="Nearby" icon={<LocationOnIcon />} />
    //   </BottomNavigation>
    // </span>
  );
}
