import React, { Component, useState, useEffect } from "react";
import Chart from "react-apexcharts";

const axios = require('axios');

class ApexChart extends React.Component {

    constructor(props) {
        super(props);

        console.log(props.coin_price)
        console.log(props.coin_times)
        const items = props.coin_times
        const ItemList = items && items.map((item, index) =>
        ({  x: new Date(item.times),
            y: [props.coin_price.minute_close[index], props.coin_price.minute_high[index], props.coin_price.minute_low[index], props.coin_price.minute_open[index]]}
        )
        );
        this.state = {

            series: [{
                data: {ItemList}
            }],
            options: {
                chart: {
                    type: 'candlestick',
                    height: 350
                },
                title: {
                    text: 'bitcoin Chart',
                    align: 'left'
                },
                xaxis: {
                    type: 'datetime'
                },
                yaxis: {
                    tooltip: {
                        enabled: true
                    }
                }
            },


        };
    }



    render() {
        return (


            <div id="chart" style={{border:'1px solid black',width:'100vw'}}>
                <Chart options={this.state.options} series={this.state.series} type="candlestick" height={350} min-width={423} margin={0}/>
            </div>


        );
    }
}


export default ApexChart;
